/**
 * Recursively searches for files with the specified extensions and runs a callback on each file,
 * excluding specified relative paths.
 * @param folderPath - The path of the folder to search within.
 * @param extensions - An array of file extensions to search for (e.g., ['.js', '.ts']).
 * @param callback - A callback function to run on each matching file.
 * @param excludePaths - An array of relative paths to exclude from the search.
 */
export declare function getFilesByExt(folderPath: string, extensions: string[], excludePaths?: string[]): Promise<string[]>;
