type Comparator<T> = (item1: T, item2: T) => number;
type ValueFunc<T> = (item: T) => number;
/**Equivalent to arr.sort().at(-1)
 *Comparison should return 1 if item1 is better */
export declare function maxBy<T>(arr: T[], fn: Comparator<T>): T | undefined;
export declare function maxBy<T>(arr: T[], fn: ValueFunc<T>): T | undefined;
/**Equivalent to arr.sort()[0]
 *Comparison should return 1 if item1 is better
 *
 */
export declare function minBy<T>(arr: T[], fn: Comparator<T>): T | undefined;
export declare function minBy<T>(arr: T[], fn: ValueFunc<T>): T | undefined;
