export type FireJetDesignTokens = Record<string, FireJetDesignToken>;
export type FireJetDesignToken = {
    type: 'static';
    content: string | number | boolean | unknown[];
    meta: DesignTokenMeta;
};
export type TailwindPrefix = Record<string, string>;
export type DesignTokenMeta = {
    firejet?: {
        tailwindThemePrefix: TailwindPrefix;
    };
};
export interface DesignLanguage {
    tokens?: FireJetDesignTokens;
}
export type TailwindStyle = {
    [styleKey: string]: TailwindStyle;
} | string;
