import { Rectangle } from '@firejet-sync/shared/types';
export type GridLine = {
    idx: number;
    type: 'START' | 'END';
};
export type RepeatGridPattern = {
    trackSizes: number[];
    gapSize: number;
};
export type GridItemProps = {
    type: 'GRID_ITEM';
    colStart: GridLine | undefined;
    colEnd: GridLine | undefined;
    rowStart: GridLine | undefined;
    rowEnd: GridLine | undefined;
    absoluteBoundingBox: Rectangle;
    x: number;
    y: number;
    width: number;
    height: number;
    nodeId: string | undefined;
};
export interface AxisProps {
    strategy: RepeatGridPattern;
    starts: number[];
    ends: number[];
    lines: number[];
}
export interface GridContainerProps {
    row: AxisProps;
    col: AxisProps;
    innerGridRect: Rectangle;
}
export interface GridProps extends GridContainerProps {
    childProps: {
        [childId: string]: GridItemProps;
    };
}
