export interface TailwindMetaData {
    fullReplacements: Record<StyleKeyVal, FullReplacement>;
    partialReplacements: Record<string, PartialReplacement>;
}
/**E.g "width:100px" With NO whitespace and only 1 colon */
export type StyleKeyVal = string;
/**The relevant tailwind class */
export type FullReplacement = string;
/**A function that takes in the CSS value and outputs the relevant class.
 * Spaces in the value must be replaced with underscores*/
export type PartialReplacement = (cleanedCssVal: string) => string;
